                <Tabs
                    value={value}
                    indicatorColor="primary"
                    textColor="primary"
                    onChange={handleChange}
                    aria-label="disabled tabs example"a
                    centered
                >
                    <Tab label="Intro" />
                    <Tab label="About" />
                    <Tab label="Contact" />
                    <Tab label="Login" />
                    <Tab label="Edition" disabled />

                </Tabs>
            </Paper>
                <Switch>
                    <Route exact path="/" component={Intro} />
                    <Route path="/about" component={About} />
