import React from 'react';
import {Component} from "react";
import {Menu, MenuItem, Icon, Grid} from '@material-ui/core';
const styles = { width: 100 };

const CustomNav = ({ active, onSelect, ...props }) => {
    return (
        <Menu {...props} vertical activeKey={active} onSelect={onSelect} style={styles}>
            <MenuItem eventKey="home" icon={<Icon icon="home" />}>
                Home
            </MenuItem>
            <MenuItem eventKey="news">News</MenuItem>
            <MenuItem eventKey="solutions">Solutions</MenuItem>
            <MenuItem eventKey="products">Products</MenuItem>
            <MenuItem eventKey="about">About</MenuItem>
        </Menu>
    );
};

export default class Article extends Component {
    constructor() {
        super();
        this.state = {
            active: 'home'
        };
        this.handleSelect = this.handleSelect.bind(this);
    }
    handleSelect(activeKey) {
        this.setState({ active: activeKey });
    }
    render() {
        const { active } = this.state;
        return (
            <Grid
                container
                direction="row"
                justify="flex-start"
                alignItems="flex-start"
            >
                <Grid item xs={4}>
                    <CustomNav active={active} onSelect={this.handleSelect} />
                </Grid>

                <Grid item xs={6}>
                    <CustomNav appearance="tabs" active={active} onSelect={this.handleSelect} />
                </Grid>
                <Grid item xs={4}>
                    <CustomNav appearance="tabs" reversed active={active} onSelect={this.handleSelect} />
                </Grid>
                <Grid item xs={6}>
                    <CustomNav appearance="subtle" active={active} onSelect={this.handleSelect} />
                </Grid>
                <Grid item xs={4}>
                    <CustomNav appearance="subtle" reversed active={active} onSelect={this.handleSelect} />
                </Grid>
            </Grid>
        );
    }
}
