import React from 'react';
import {BrowserRouter as Router, Switch, Link, Route} from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
// import Button from '@material-ui/core/Button';
import MenuIcon from '@material-ui/icons/Menu';
import PropTypes from 'prop-types';
import { AppBar, Typography, Toolbar, Tabs, Tab, Box, IconButton, Grid } from '@material-ui/core';
import Intro from "./intro";
import Contact from "./contacts";
import About from "./about";
import Auth from "./auth";
import {FacebookIcon, InstagramIcon, YouTubeIcon} from "@material-ui/icons/Facebook";
import PasswordReset from "./passwordReset";
import EditionMenu from "./leftMenu";
import Edition from "./edition";


function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <Typography
            component="div"
            role="tabpanel"
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && <Box p={3}>{children}</Box>}
        </Typography>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    };
}

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),

    },
    title: {
        flexGrow: 1,
    },
    tab: {
        justifyContent: 'center',
        width: '100%',
    },
    tabs: {
        display: 'flex',
        justifyContent: 'space-around',
        width: '100%',
    },
}));

export default function MenuBar() {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <div className={classes.root}>
            <AppBar position="static">
                <Toolbar className={classes.tabs}>
                    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                        <EditionMenu/>
                    </IconButton>
                    <Router>
                    <Tabs value={value}  onChange={handleChange} aria-label="simple tabs example">

                        <Link to="/">
                            <Tab label="Intro" {...a11yProps(0)} />
                        </Link>
                        <Link to="/about">
                            <Tab label="About" {...a11yProps(1)} />
                        </Link>
                        <Link to="/contact">
                            <Tab label="Contacts" {...a11yProps(2)} />
                        </Link>
                        <Link to="/login">
                            <Tab label="Login" {...a11yProps(3)} />
                        </Link>
                        <Link to="/test">
                            <Tab label="Test" {...a11yProps(4)} />
                        </Link>

                    </Tabs>
                    </Router>
                    <Grid>
                        <FacebookIcon />
                        <InstagramIcon />
                        <YouTubeIcon />
                    </Grid>
                </Toolbar>
            </AppBar>
            <div >
                <Switch>
                    <Route exact path="/">
                        <TabPanel value={value} index={0}>
                            <Intro />
                        </TabPanel>
                    </Route>
                    <Route>
                        <TabPanel value={value} index={1}>
                            <About />
                        </TabPanel>
                    </Route>
                    <Route>
                        <TabPanel value={value} index={2}>
                            <Contact />
                        </TabPanel>
                    </Route>
                    <Route>
                        <TabPanel value={value} index={3}>
                            <Auth />
                            {/*<Article />*/}
                        </TabPanel>
                    </Route>
                    <Route>
                        <TabPanel value={value} index={4}>
                            {/*<PasswordReset />*/}
                            <Edition />
                        </TabPanel>
                    </Route>
                </Switch>
            </div>
        </div>
    );
}
