import React, {Component} from 'react';
import {BrowserRouter as Router, Switch, Link, Route} from "react-router-dom";
import { makeStyles } from '@material-ui/core/styles';
// import Button from '@material-ui/core/Button';
import MenuIcon from '@material-ui/icons/Menu';
import PropTypes from 'prop-types';
import { AppBar, Typography, Toolbar, Tabs, Tab, Box, IconButton, Grid } from '@material-ui/core';
import Intro from "./intro";
import Contact from "./contacts";
import About from "./about";
import Auth from "./auth";
import {FacebookIcon, InstagramIcon, YouTubeIcon} from "@material-ui/icons/Facebook";
import PasswordReset from "./passwordReset";
import EditionMenu from "./leftMenu";
import Edition from "./edition";
import {
    Redirect,
    useHistory,
    useLocation
} from "react-router-dom";
import SignIn from "./singIn";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles({
    root: {
        flexGrow: 1,
    },
    button: {

    }
});
export default function MenuBar() {


    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <div>
        <Router>
                {/*<Paper className={classes.root}>*/}
                    <Tabs
                        value={value}
                        onChange={handleChange}
                        indicatorColor="primary"
                        textColor="primary"
                        centered
                    >
                        <EditionMenu />
                        <Link to="/"><Tab label={Intro} />Intro</Link>
                        <Link to="/about"><Tab label={About} />About</Link>
                        <Tab label={<Link to="/contact">Contacts</Link>} />
                        <Tab label={<Link to="/auth">Login</Link>} />
                        <Tab label={<Link to="/edition">Edition</Link>} />

                    </Tabs>
                {/*</Paper>*/}

                <Switch>
                    <Route exact path="/">
                        <Intro />
                    </Route>
                    <Route path="/about">
                        <About />
                    </Route>
                    <Route path="/contact">
                        <Contact />
                    </Route>
                    <Route path="/auth">
                        <Auth />
                    </Route>
                    <PrivateRoute path="/edition">
                        <Edition />
                    </PrivateRoute>
                </Switch>
        </Router>
        </div>
    );
}

