import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
// import Button from '@material-ui/core/Button';
import MenuIcon from '@material-ui/icons/Menu';
import PropTypes from 'prop-types';
import { AppBar, Typography, Toolbar, Tabs, Tab, Box, IconButton, Grid, Link } from '@material-ui/core';
import Intro from "./intro";
import Contact from "./contacts";
import About from "./about";
import Auth from "./auth";
import Location from "./map";
import FacebookIcon from "@material-ui/icons/Facebook";
import InstagramIcon from "@material-ui/icons/Instagram";
import YouTubeIcon from "@material-ui/icons/YouTube";
import Article from "./articles";
import PasswordReset from "./passwordReset";
import Edition from "./editions";


function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <Typography
            component="div"
            role="tabpanel"
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            {value === index && <Box p={3}>{children}</Box>}
        </Typography>
    );
}

TabPanel.propTypes = {
    children: PropTypes.node,
    index: PropTypes.any.isRequired,
    value: PropTypes.any.isRequired,
};

function a11yProps(index) {
    return {
        id: `simple-tab-${index}`,
        'aria-controls': `simple-tabpanel-${index}`,
    };
}

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),

    },
    title: {
        flexGrow: 1,
    },
    tab: {
        justifyContent: 'center',
        width: '100%',
    },
    tabs: {
        display: 'flex',
        justifyContent: 'space-around',
        width: '100%',
    },
}));

export default function MenuBar() {
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    const handleChange = (event, newValue) => {
        setValue(newValue);
    };

    return (
        <div className={classes.root}>
            <AppBar position="static">
                <Toolbar className={classes.tabs}>
                    <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                        <MenuIcon />
                        <Edition/>
                    </IconButton>
                    <Tabs value={value}  onChange={handleChange} aria-label="simple tabs example">
                        <Tab label="Intro" {...a11yProps(0)} />
                        <Tab label="About" {...a11yProps(1)} />
                        <Tab label="Contacts" {...a11yProps(2)} />
                        <Tab label="Login" {...a11yProps(3)} />
                        <Tab label="Test" {...a11yProps(4)} />
                    </Tabs>
                    <Grid>
                        <FacebookIcon />
                        <InstagramIcon />
                        <YouTubeIcon />
                    </Grid>
                </Toolbar>
            </AppBar>
            <div >
                <TabPanel value={value} index={0}>
                    <Intro />
                </TabPanel>
                <TabPanel value={value} index={1}>
                    <About />
                </TabPanel>
                <TabPanel value={value} index={2}>
                    <Contact />
                </TabPanel>
                <TabPanel value={value} index={3}>
                    <Auth />
                    {/*<Article />*/}
                </TabPanel>
                <TabPanel value={value} index={4}>
                    {/*<PasswordReset />*/}
                    <Article />
                </TabPanel>
            </div>
        </div>
    );
}
